    private void BTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTNActionPerformed
        if (register) { //para registrar
        String username = usernameFIELD.getText();
        String tipoUsuario = (String) tipoBox.getSelectedItem();
        int edad = Integer.parseInt((String) edadBox.getSelectedItem());

        try {
            Steam steam = new Steam();
            steam.addPlayer(username, edad, tipoUsuario);
            JOptionPane.showMessageDialog(null, "¡Jugador registrado exitosamente!");
        } catch (IOException e) {
            
            e.printStackTrace();
            JOptionPane.showMessageDialog(null, "Error al registrar el jugador. Por favor, inténtalo de nuevo.");
        }
    } else if (modify) { // para modificar
        String username = usernameFIELD.getText();
        String tipoUsuario = (String) tipoBox.getSelectedItem();
        int edad = Integer.parseInt((String) edadBox.getSelectedItem());

        
        try {
            Steam steam = new Steam(); 
            steam.modifyPlayer(username, edad, tipoUsuario);
            JOptionPane.showMessageDialog(null, "¡Jugador modificado exitosamente!");
        } catch (IOException e) {
            
            e.printStackTrace();
            JOptionPane.showMessageDialog(null, "Error al modificar el jugador. Por favor, inténtalo de nuevo.");
        }
    } else if (delete) { // para borrar
        // Lógica para eliminar un usuario
    }
    }//GEN-LAST:event_BTNActionPerformed

