    void setEverything(){
        setBox();
        setPanel();
    }
    
    void setBox(){
        tipoBox.removeAllItems();
        tipoBox.addItem("Normal");
        tipoBox.addItem("Admin");
        
        edadBox.removeAllItems();
        for(int i=1; i<=100; i++){
            String age=String.valueOf(i);
            edadBox.addItem(age);
        }
    }
    
    void setPanel(){
        usernameFIELD.setVisible(false);
            usernameLABEL.setVisible(false);
            tipoBox.setVisible(false);
            tipoLABEL.setText("Welcome to settings");
            BTN.setVisible(false);
            edadBox.setVisible(false);
            edadLABEL.setVisible(false);
    }
    
    void Action(){
        if(delete==true){
            usernameFIELD.setVisible(false);
            usernameLABEL.setVisible(false);
            tipoBox.setVisible(false);
            tipoLABEL.setText("Eliminar cuenta?");
            BTN.setText("Delete");
            BTN.setVisible(true);
            edadBox.setVisible(false);
            edadLABEL.setVisible(false);
        }else if(modify==true){
            usernameFIELD.setVisible(true);
            usernameLABEL.setVisible(true);
            tipoBox.setVisible(true);
            tipoLABEL.setText("Tipo");
            BTN.setText("Modify");
            BTN.setVisible(true);
            edadBox.setVisible(true);
            edadLABEL.setVisible(true);
        }else if(register==true){
            usernameFIELD.setVisible(true);
            usernameLABEL.setVisible(true);
            tipoBox.setVisible(true);
            tipoLABEL.setText("Tipo");
            BTN.setText("Register");
            BTN.setVisible(true);
            edadBox.setVisible(true);
            edadLABEL.setVisible(true);
        }
    }

